import React from 'react';
import Sophie from "../assets/images/Sophie-v2.PNG";
import { NavLink } from 'react-router-dom'

const Portfolio = () => {
  return (
    <section id="portfolio">
      <h2>Portfolio</h2>
			<div className="services-all">

        <div className="portfolio-block">
        <img src={Sophie} alt="Portfolio"/>
					<div className="porfolio-description">
						<div className="porfolio-title-card">
							<h3>Portfolio</h3>
						</div>
						<p> Récupération des données de l'API. Intégration des fonctionnalités de filtrage d'images. Page de connexion pour l'administrateur du site. Modalité d'upload et de supression de médias qui permet une mise à jour facile et rapide du portfolio.
							<br/>
							<br/>
							Javascript , CSS, Swagger UI  </p>
<NavLink href="https://github.com/ClementMadiot/OC-Portfolio-Sophie-Bluel-P3" target="_blank" class="button">Repo Github></NavLink>
						
					</div>
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
